const themeSlice = createSlice({
  name: "@@theme",
  initialState: "light",
  reducer: {
    setTheme: (_, action) => action.payload,
  },
});

export const { setTheme } = themeSlice.actions;
export const themeReducer = themeSlice.reducer;
