import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
import {
  initializeSwitch,
  toggle,
  toggleSwitch,
} from "../features/countries/countries-slice";
import { useEffect } from "react";

const Wrapper = styled.article`
  border-radius: var(--radii);
  background-color: var(--colors-ui-base);
  box-shadow: var(--shadow);
  cursor: pointer;
  overflow: hidden;
`;

const CardImage = styled.img`
  display: block;
  object-fit: cover;
  object-position: center;
  max-height: 50px;
  // box-shadow: var(--shadow);
`;

const CardBody = styled.div`
  padding: 0px 10px 0px 10px;
`;

const CardTitle = styled.h3`
  margin: 0;
  font-size: var(--fs-md);
  font-weight: var(--fw-bold);
`;

const CardList = styled.ul`
  list-style: none;
  margin: 0;
  padding: 5px 0 0;
`;

const CardListItem = styled.li`
  font-size: var(--fs-sm);
  line-height: 1.5;
  font-weight: var(--fw-light);

  & > b {
    font-weight: var(--fw-bold);
  }
`;

const CardInfo = styled.div`
  display: flex;
  padding: 14px 14px 0px 14px;
`;

const CardBtn = styled.button`
  background-color: #cccccc; /* серый цвет */
  border: none; /* убираем рамку */
  padding: 8px 16px; /* внутренние отступы */
  border-radius: 15px; /* чуть закруглённые углы */
  cursor: pointer; /* курсор при наведении */
  font-size: 14px; /* размер шрифта */
  background-color: var(--colors-ui-base);
  border: 0.1px solid white;
  color: white;
  margin: 10px 0px 10px 14px;
`;
const CardGot = styled.div`
  display: flex;
`;
const CardSwitch = styled.div``;

export const Card = ({ logo, id, name, info = [] }) => {
  const dispatch = useDispatch();

  // Инициализация состояния для этого переключателя, если его нет
  useEffect(() => {
    dispatch(initializeSwitch(id));
  }, [dispatch, id]);

  const checked = useSelector((state) => state.toggle.switches[id]);

  const handleClick = () => {
    dispatch(toggleSwitch(id));
  };
  return (
    <Wrapper>
      <CardInfo>
        <CardImage src={logo} alt={name} />
        <CardBody>
          <CardTitle>{name}</CardTitle>
          <CardList>
            {info.map((el) => (
              <CardListItem key={el.name}>
                <b>{el.description}</b>
              </CardListItem>
            ))}
          </CardList>
        </CardBody>
      </CardInfo>
      <CardGot>
        <CardBtn>Remove</CardBtn>
        <CardSwitch key={logo}>
          <div
            onClick={handleClick}
            style={{
              width: "60px",
              height: "30px",
              backgroundColor: "#ccc",
              borderRadius: "15px",
              position: "relative",
              cursor: "pointer",
              transition: "background-color 0.3s",
              display: "flex",
              alignItems: "center",
              padding: "2px",
            }}
          >
            <div
              style={{
                width: "26px",
                height: "26px",
                backgroundColor: "white",
                borderRadius: "50%",
                transition: "left 0.3s",
                position: "absolute",
                top: "2px",
                left: checked ? "32px" : "2px",
              }}
            />
          </div>
        </CardSwitch>
      </CardGot>
    </Wrapper>
  );
};
